import React from "react";

const loginStyle = {
  position: "relative",
  width: "23vw",
  height: "10vh",
  fontSize: "2vh",
  border: "none",
  borderBottom: "2px solid #00282a",
  backgroundColor: "transparent",
};

const LoginForm = () => {
  return (
    <div>
      <form class="ui form" style={{ top: "28vh", left: "5vw" }}>
        <div class="field">
          <input
            type="email"
            name="email"
            placeholder="Email"
            style={loginStyle}
          ></input>
        </div>
        <div class="field">
          <input
            type="password"
            name="password"
            placeholder="Password"
            style={loginStyle}
          ></input>
        </div>
        <button
          className="ui button"
          type="submit"
          style={{ top: "28vh", left: "5vw" }}
        >
          Submit
        </button>
      </form>
    </div>
  );
};

export default LoginForm;
