import React, { useState } from "react";
import DBoardTemplate from "./DBoardTemplate";
import { Grid, Menu, Segment, Icon } from "semantic-ui-react";

const AdvocateDBoard = () => {
  const [activeItem, setActiveItem] = useState("overview");

  const handleItemClick = (e, { name }) => {
    setActiveItem(name);
  };

  const agency = "Jane Doe Agency";

  // import content logic in here
  const renderSwitch = (state) => {
    switch (state) {
      case "overview":
        return "Overview menu";
      case "new":
        return (
          <div>
            <iframe
              title="test"
              width="640px"
              height="480px"
              src="https://forms.office.com/Pages/ResponsePage.aspx?id=llv3iFRLPEyxLfZUBQzoza2vf3U6VrhDgvKz0B6i_dtUQ0JBMVVRV0JTN0lYQ0xITVYyTEs2T1ZBSC4u&embed=true"
              frameborder="0"
              marginwidth="0"
              marginheight="0"
            ></iframe>{" "}
          </div>
        );
      case "visualization":
        return (
          <div>
            <iframe
              title="test graph"
              width="453"
              height="311"
              frameborder="0"
              scrolling="no"
              src="https://onedrive.live.com/embed?resid=7A6F419AC957F9E2%2111335&authkey=%21AJY3ZnSG3HVVKns&em=2&wdAllowInteractivity=False&Item=Chart%201&wdDownloadButton=True&wdInConfigurator=True"
            ></iframe>
          </div>
        );
      case "settings":
        return "Settings menu";
      default:
        return "Error Occured, contact system admin";
    }
  };

  const content = (
    <Grid style={{ height: "100vh" }}>
      <Grid.Column width={4} style={{ backgroundColor: "rgb(250, 251, 252)" }}>
        <Menu fluid vertical tabular color="blue">
          <div className="center item">
            <h3>Advocate</h3>
            {agency}
          </div>
          <Menu.Item
            name="overview"
            active={activeItem === "overview"}
            onClick={handleItemClick}
          >
            Overview <Icon name="clone" />
          </Menu.Item>
          <Menu.Item
            name="new"
            active={activeItem === "new"}
            onClick={handleItemClick}
          >
            New Submission <Icon name="plus" />
          </Menu.Item>
          <Menu.Item
            name="visualization"
            active={activeItem === "visualization"}
            onClick={handleItemClick}
          >
            Visualization <Icon name="line graph" />
          </Menu.Item>
          <Menu.Item
            name="settings"
            active={activeItem === "settings"}
            onClick={handleItemClick}
          >
            Setting <Icon name="cog" />
          </Menu.Item>
        </Menu>
      </Grid.Column>

      <Grid.Column stretched width={12}>
        <Segment>{renderSwitch(activeItem)}</Segment>
      </Grid.Column>
    </Grid>
  );

  return (
    <div>
      <DBoardTemplate content={content} />
    </div>
  );
};

export default AdvocateDBoard;
