import React, { useState } from "react";
import { Dropdown } from "semantic-ui-react";

const yearOptions = [
  {
    key: 2017,
    text: 2017,
    value: 2017,
  },
  {
    key: 2018,
    text: 2018,
    value: 2018,
  },
  {
    key: 2019,
    text: 2019,
    value: 2019,
  },
  {
    key: 2020,
    text: 2020,
    value: 2020,
  },
  {
    key: 2021,
    text: 2021,
    value: 2021,
  },
];

const Visualization = () => {
  const year2017 =
    "https://thecdm.sharepoint.com/sites/team_mpath/_layouts/15/Doc.aspx?sourcedoc={de5298a6-d470-4a61-8501-2ddd887618ed}&action=embedview&wdAllowInteractivity=False&ActiveCell='Charts'!A1&wdHideGridlines=True&wdHideHeaders=True&wdDownloadButton=True&wdInConfigurator=True";
  const year2018 =
    "https://thecdm.sharepoint.com/sites/team_mpath/_layouts/15/Doc.aspx?sourcedoc={d23cc641-6914-4f1b-bcaa-928ac9103782}&action=embedview&wdAllowInteractivity=False&ActiveCell='Charts'!A1&wdHideGridlines=True&wdHideHeaders=True&wdDownloadButton=True&wdInConfigurator=True";
  const year2019 =
    "https://thecdm.sharepoint.com/sites/team_mpath/_layouts/15/Doc.aspx?sourcedoc={1246040f-4273-4d30-8a83-500f3cbfceec}&action=embedview&wdAllowInteractivity=False&ActiveCell='Charts'!A1&wdHideGridlines=True&wdHideHeaders=True&wdDownloadButton=True&wdInConfigurator=True";
  const year2020 =
    "https://thecdm.sharepoint.com/sites/team_mpath/_layouts/15/Doc.aspx?sourcedoc={5527e969-5201-4604-9fed-5c4e7c97db2a}&action=embedview&wdAllowInteractivity=False&wdHideGridlines=True&wdHideHeaders=True&wdDownloadButton=True&wdInConfigurator=True";
  const year2021 =
    "https://thecdm.sharepoint.com/sites/team_mpath/_layouts/15/Doc.aspx?sourcedoc={2d914d20-4d46-48c5-87da-54448e8abae4}&action=embedview&wdAllowInteractivity=False&wdHideGridlines=True&wdHideHeaders=True&wdDownloadButton=True&wdInConfigurator=True";

  const [activeYear, setActiveYear] = useState(2020);

  const handleItemClick = (e, { value }) => {
    setActiveYear(value);
  };

  const switchYear = (year) => {
    switch (year) {
      case 2017:
        return year2017;
      case 2018:
        return year2018;
      case 2019:
        return year2019;
      case 2020:
        return year2020;
      case 2021:
        return year2021;
      default:
        return year2020;
    }
  };

  return (
    <div>
      <Dropdown
        placeholder="Select Year"
        selection
        options={yearOptions}
        onChange={handleItemClick}
        value={activeYear}
      />
      <embed
        style={{ width: "72vw", height: "100vh" }}
        src={switchYear(activeYear)}
      ></embed>
    </div>
  );
};

export default Visualization;
