import React, { useEffect, useState } from "react";
import DBoardTemplate from "./DBoardTemplate";
import { Grid, Menu, Segment, Icon } from "semantic-ui-react";
import { useAuth0 } from "@auth0/auth0-react";

const AdvocateDBoard = () => {
  const { user, getAccessTokenSilently } = useAuth0();

  const domain = process.env.REACT_APP_AUTH0_DOMAIN;

  const [role, setRole] = useState({});

  const [activeItem, setActiveItem] = useState("overview");

  useEffect(() => {
    const getUserMetadata = async () => {
      try {
        getAccessTokenSilently({
          audience: `https://${domain}/api/v2/`,
          scope: "read:current_user",
        })
          .then((accessToken) => {
            const userDetailsByIdUrl = `https://${domain}/api/v2/users/${user.sub}`;

            fetch(userDetailsByIdUrl, {
              headers: {
                Authorization: `Bearer ${accessToken}`,
              },
            }).then((metadataResponse) => {
              metadataResponse.json().then((result) => {
                setRole(result.app_metadata.role);
                const roleString = String(role);
                if (roleString === "advocate") {
                  window.location.href = `${window.location.origin}/advocate`;
                } else {
                  window.location.href = `${window.location.origin}/advocate`;
                }
              });
            });
          })
          .catch((e) => {
            console.log(e);
          });
      } catch (e) {
        console.log(e);
      }
    };
    if (user) {
      getUserMetadata();
    }
  }, [domain, getAccessTokenSilently, user, role]);

  const handleItemClick = (e, { name }) => {
    setActiveItem(name);
  };

  const agency = user.email;

  // import content logic in here
  const renderSwitch = (state) => {
    switch (state) {
      case "overview":
        return "Overview menu";
      case "new":
        return "New Submission menu";
      case "visualization":
        return "Visualization menu";
      case "settings":
        return "Settings menu";
      default:
        return "Error Occured, contact system admin";
    }
  };

  const content = (
    <Grid style={{ height: "100vh" }}>
      <Grid.Column width={4} style={{ backgroundColor: "rgb(250, 251, 252)" }}>
        <Menu fluid vertical tabular color="blue">
          <div className="center item">
            <h3>Advocate</h3>
            {agency}
          </div>
          <Menu.Item
            name="overview"
            active={activeItem === "overview"}
            onClick={handleItemClick}
          >
            Overview <Icon name="clone" />
          </Menu.Item>
          <Menu.Item
            name="new"
            active={activeItem === "new"}
            onClick={handleItemClick}
          >
            New Submission <Icon name="plus" />
          </Menu.Item>
          <Menu.Item
            name="visualization"
            active={activeItem === "visualization"}
            onClick={handleItemClick}
          >
            Visualization <Icon name="line graph" />
          </Menu.Item>
          <Menu.Item
            name="settings"
            active={activeItem === "settings"}
            onClick={handleItemClick}
          >
            Setting <Icon name="cog" />
          </Menu.Item>
        </Menu>
      </Grid.Column>

      <Grid.Column stretched width={12}>
        <Segment>{renderSwitch(activeItem)}</Segment>
      </Grid.Column>
    </Grid>
  );

  return (
    <div>
      <DBoardTemplate content={content} />
    </div>
  );
};

export default AdvocateDBoard;
