import React from "react";
import LogoutButton from "./LogoutButton";
import { useAuth0 } from "@auth0/auth0-react";
import { useEffect } from "react";

const DBoardTemplate = (props) => {
  const { user, getAccessTokenSilently } = useAuth0();

  const domain = process.env.REACT_APP_AUTH0_DOMAIN;

  useEffect(() => {
    const getUserMetadata = async () => {
      try {
        getAccessTokenSilently({
          audience: `https://${domain}/api/v2/`,
          scope: "read:current_user",
        })
          .then((accessToken) => {
            const userDetailsByIdUrl = `https://${domain}/api/v2/users/${user.sub}`;

            fetch(userDetailsByIdUrl, {
              headers: {
                Authorization: `Bearer ${accessToken}`,
              },
            }).then((metadataResponse) => {
              metadataResponse.json().then((result) => {
                console.log(result.app_metadata);
              });
            });
          })
          .catch((e) => {
            console.log(e);
          });
      } catch (e) {
        console.log(e);
      }
    };
    if (user) {
      //console.log(user.sub);
      getUserMetadata();
    }
  }, [domain, getAccessTokenSilently, user]);

  return (
    <div>
      <div className="ui borderless massive menu">
        <div className="header item">
          <img
            className="ui image"
            src={process.env.PUBLIC_URL + "/extracy.png"}
            alt="team logo"
            style={{ width: "100px" }}
          />
        </div>
        <div className="right item">
          <a href="https://www.google.ca/" className="vertically fitted item">
            <i className="question circle icon"></i>
          </a>
          <LogoutButton />
        </div>
      </div>

      {props.content}
    </div>
  );
};

export default DBoardTemplate;
