import React from "react";
import { useAuth0 } from "@auth0/auth0-react";
import LoginButton from "./LoginButton";
import LogoutButton from "./LoginButton";

const loginStyle = {
  position: "relative",
  width: "23vw",
  height: "10vh",
  fontSize: "2vh",
  border: "none",
  borderBottom: "2px solid #00282a",
  backgroundColor: "transparent",
};

const AuthNav = () => {
  const { isAuthicated } = useAuth0();

  return (
    <div className="justify-content-end">
      {isAuthicated ? <LogoutButton /> : <LoginButton />}
    </div>
  );
};

const LoginForm = () => {
  return (
    <div>
      <form className="ui form" style={{ top: "28vh", left: "5vw" }}>
        <div className="field">
          <input
            type="email"
            name="email"
            placeholder="Email"
            style={loginStyle}
          ></input>
        </div>
        <div className="field">
          <input
            type="password"
            name="password"
            placeholder="Password"
            style={loginStyle}
          ></input>
        </div>
        <AuthNav />
        <button>Login</button>
        <p style={{ marginLeft: "9vh", color: "#748281" }}>
          Forgot ID or password?
        </p>
      </form>
    </div>
  );
};

export default LoginForm;
