import "semantic-ui-css/semantic.min.css";
import AdvocateDBoard from "./dashboards/AdvocateDBoard";
import A2JDBoard from "./dashboards/DBoardTemplate";
import LoginPage from "./login/LoginPage";
import { useLocation, Switch, Route } from "react-router-dom";
import PrivateRoute from "./private-route";
import { useEffect } from "react";
import { useAuth0 } from "@auth0/auth0-react";

function App() {
  const location = useLocation();
  const { isAuthenticated } = useAuth0();

  useEffect(() => {
    console.log(location.pathname);
    if (!isAuthenticated) {
      window.location.href = `${window.location.origin}/advocate`;
    }
  }, [location, isAuthenticated]);

  return (
    <div className="App">
      <Switch>
        <PrivateRoute exact path="/advocate" component={AdvocateDBoard} />
        <PrivateRoute exact path="/a2j" component={A2JDBoard} />
        <Route exact path="/" component={LoginPage} />
      </Switch>
    </div>
  );
}

export default App;
