import React from "react";
import advocateOverview from "./advocateOverview.png";

const Overview = () => {
  const date = new Date();
  const month_num = date.getMonth();
  const months = {
    0: "January",
    1: "February",
    2: "March",
    3: "April",
    4: "May",
    5: "June",
    6: "July",
    7: "August",
    8: "September",
    9: "October",
    10: "November",
    11: "December",
  };
  const month = months[month_num];

  return (
    <div
      style={{
        width: "100%",
        height: "100%",
        fontSize: "4vh",
        textAlign: "center",
        verticalAlign: "70vh",
        backgroundImage: `url(${advocateOverview})`,
      }}
    >
      It is currently {month}, have you submitted your data for this month yet?
    </div>
  );
};

export default Overview;
