import React from "react";
import { useHistory } from "react-router";
import { Auth0Provider } from "@auth0/auth0-react";

const Auth0ProviderWithHistoryA2J = ({ children }) => {
  const history = useHistory();
  const domain = process.env.REACT_APP_AUTH0_DOMAIN_A2J;
  const clientId = process.env.REACT_APP_AUTH0_CLIENT_ID_A2J;

  const onRedirectCallback = (appState) => {
    history.push(appState?.returnTo || window.location.pathname);
  };

  return (
    <Auth0Provider
      domain={domain}
      clientId={clientId}
      redirectUri={`${window.location.origin}/a2j`}
      onRedirectCallback={onRedirectCallback}
    >
      {children}
    </Auth0Provider>
  );
};

export default Auth0ProviderWithHistoryA2J;
