import React, { useEffect } from "react";
import { useAuth0 } from "@auth0/auth0-react";
import LoginButton from "./LoginButton";
import LogoutButton from "../../../dashboards/LogoutButton";

// const loginStyle = {
//   position: "relative",
//   width: "23vw",
//   height: "10vh",
//   fontSize: "2vh",
//   border: "none",
//   borderBottom: "2px solid #00282a",
//   backgroundColor: "transparent",
// };

const AuthNav = (props) => {
  const { setRole } = props;

  const { isAuthenticated, user, getAccessTokenSilently } = useAuth0();

  const domain = process.env.REACT_APP_AUTH0_DOMAIN;

  useEffect(() => {
    const getUserMetadata = async () => {
      try {
        getAccessTokenSilently({
          audience: `https://${domain}/api/v2/`,
          scope: "read:current_user",
        })
          .then((accessToken) => {
            const userDetailsByIdUrl = `https://${domain}/api/v2/users/${user.sub}`;

            fetch(userDetailsByIdUrl, {
              headers: {
                Authorization: `Bearer ${accessToken}`,
              },
            }).then((metadataResponse) => {
              metadataResponse.json().then((result) => {
                setRole(result.app_metadata.role);
              });
            });
          })
          .catch((e) => {
            console.log(e);
          });
      } catch (e) {
        console.log(e);
      }
    };
    if (user) {
      getUserMetadata();
    }
  }, [domain, getAccessTokenSilently, user, setRole]);

  return <div>{isAuthenticated ? <LogoutButton /> : <LoginButton />}</div>;
};

const LoginForm = (props) => {
  const { setRole } = props;
  console.log(setRole);
  return (
    <div>
      <form className="ui form" style={{ top: "28vh", left: "5vw" }}>
        {/* <div className="field">
          <input
            type="email"
            name="email"
            placeholder="Email"
            style={loginStyle}
          ></input>
        </div>
        <div className="field">
          <input
            type="password"
            name="password"
            placeholder="Password"
            style={loginStyle}
          ></input>
        </div> */}
        <AuthNav setRole={setRole} />
      </form>
    </div>
  );
};

export default LoginForm;
