import "semantic-ui-css/semantic.min.css";
import AdvocateDBoard from "./dashboards/AdvocateDBoard";
import A2JDBoard from "./dashboards/DBoardTemplate";
import LoginPage from "./login/LoginPage";
import { useLocation, Switch, Route } from "react-router-dom";
import PrivateRoute from "./private-route";
import { useEffect, useState } from "react";
import { useAuth0 } from "@auth0/auth0-react";

function App() {
  // const location = useLocation();

  // useEffect(() => {
  //   console.log(location.pathname);
  // }, [location]);
  const { user, getAccessTokenSilently } = useAuth0();

  const domain = process.env.REACT_APP_AUTH0_DOMAIN;

  const [role, setRole] = useState({});

  useEffect(() => {
    const getUserMetadata = async () => {
      try {
        getAccessTokenSilently({
          audience: `https://${domain}/api/v2/`,
          scope: "read:current_user",
        })
          .then((accessToken) => {
            const userDetailsByIdUrl = `https://${domain}/api/v2/users/${user.sub}`;

            fetch(userDetailsByIdUrl, {
              headers: {
                Authorization: `Bearer ${accessToken}`,
              },
            }).then((metadataResponse) => {
              metadataResponse.json().then((result) => {
                setRole(result.app_metadata.role);
                const roleString = String(role);
                if (roleString === "advocate") {
                  window.location.href = `${window.location.origin}/advocate`;
                }
                if (roleString === "a2j") {
                  window.location.href = `${window.location.origin}/a2j`;
                }
              });
            });
          })
          .catch((e) => {
            console.log(e);
          });
      } catch (e) {
        console.log(e);
      }
    };
    if (user) {
      getUserMetadata();
    }
  }, [domain, getAccessTokenSilently, user, role]);

  return (
    <div className="App">
      <Switch>
        <PrivateRoute exact path="/advocate" component={AdvocateDBoard} />
        <PrivateRoute exact path="/a2j" component={A2JDBoard} />
        <Route exact path="/" component={LoginPage} />
      </Switch>
    </div>
  );
}

export default App;
