import React from "react";
import { useAuth0 } from "@auth0/auth0-react";
import LoginButton from "./LoginButton";
import { useEffect } from "react";
import Auth0Lock from "auth0-lock";

const loginStyle = {
  position: "relative",
  width: "23vw",
  height: "10vh",
  fontSize: "2vh",
  border: "none",
  borderBottom: "2px solid #00282a",
  backgroundColor: "transparent",
};

const AuthNav = () => {
  const { isAuthenticated, user, getAccessTokenSilently } = useAuth0();

  const domain = process.env.REACT_APP_AUTH0_DOMAIN;

  useEffect(() => {
    const getUserMetadata = async () => {
      try {
        getAccessTokenSilently({ scope: "read:current_user" }).then(
          (accessToken) => {
            console.log(`access token: ${accessToken}`);
            const userDetailsByIdUrl = `https://${domain}/api/v2/users/${user.sub}`;
            console.log("boop");
            fetch(userDetailsByIdUrl, {
              headers: {
                Authorization: `Bearer ${accessToken}`,
              },
            }).then((metadataResponse) => {
              metadataResponse.json().then((result) => {
                console.log(result.user_metadata);
              });
            });
          }
        );
        // const accessToken = await getAccessTokenSilently({
        //   audience: `https://${domain}/api/v2/`,
        //   scope: "read:current_user",
        // });
      } catch (e) {
        console.log(e);
      }
    };
    if (user) {
      console.log(user.sub);
      getUserMetadata();
    }
  }, [domain, getAccessTokenSilently, user]);

  return (
    <div className="justify-content-end">
      {isAuthenticated ? "is authenticated" : <LoginButton />}
    </div>
  );
};

const LoginForm = () => {
  return (
    <div>
      <form className="ui form" style={{ top: "28vh", left: "5vw" }}>
        <div className="field">
          <input
            type="email"
            name="email"
            placeholder="Email"
            style={loginStyle}
          ></input>
        </div>
        <div className="field">
          <input
            type="password"
            name="password"
            placeholder="Password"
            style={loginStyle}
          ></input>
        </div>
        <AuthNav />
      </form>
    </div>
  );
};

export default LoginForm;
