];

const Visualization = () => {
  const [activeYear, setActiveYear] = useState(2020);

  const handleItemClick = (e, { year }) => {
    setActiveYear(year);
  };

  return (
    <div>
      <Dropdown
        placeholder="Select Year"
        selection
        options={yearOptions}
        onChange={handleItemClick}
        value={activeYear}
      />
      {activeYear}
    </div>
  );
};

export default Visualization;
