import React, { useState } from "react";
import { Dropdown } from "semantic-ui-react";

const yearOptions = [
  {
    key: 2017,
    text: 2017,
    value: 2017,
  },
  {
    key: 2018,
    text: 2018,
    value: 2018,
  },
  {
    key: 2019,
    text: 2019,
    value: 2019,
  },
  {
    key: 2020,
    text: 2020,
    value: 2020,
  },
  {
    key: 2021,
    text: 2021,
    value: 2021,
  },
];

const Visualization = () => {
  const [activeYear, setActiveYear] = useState(2020);

  const handleItemClick = (e, { value }) => {
    setActiveYear(value);
  };

  return (
    <div>
      <Dropdown
        placeholder="Select Year"
        selection
        options={yearOptions}
        onChange={handleItemClick}
        value={activeYear}
      />
      {activeYear}
    </div>
  );
};

export default Visualization;
